import React from 'react'
import classes from './create.module.css'
import {AiOutlineCloseCircle} from 'react-icons/ai'
import { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { useSelector } from 'react-redux'

const Create = () => {
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [category, setCategory] = useState("")
  const [image, setImage] = useState("")
  const [Price, setPrice] = useState("")
  const [review, setReview] = useState("")
  const navigate = useNavigate()

  //We get the auth slice from the entire state, which(auth slice)
  //is the userInfo and the token
  const {token} = useSelector((state)=>state.auth)

  //type="file", e.target.file[0]
  const onChangeFile = (e) => {
    setImage(e.target.files[0])
  }

  const handleCloseImg = (e) => {
    setImage('')
  }

  const handleCreateProduct = async(e) => {
    e.preventDefault('')
    
    try {
       const formData = new FormData()
       let filename = null

       if(image){
        filename = Date.now() + image.name
        formData.append("filename", filename)
        formData.append("image", image)

        await fetch(`http://localhost:5000/upload/image`, {
          headers: {
            "Authorization": `Bearer ${token}`
          },
          method: "POST",
          body: formData
        })
       }

      //uploading product
      const res = await fetch(`http://localhost:5000/product`, {
        headers: {
          'Authorization' : `Bearer ${token}`
        },
        method: 'POST',
        body: JSON.stringify({
          title,
          desc,
          category,
          img: filename,
          Price,
          review
        })
      })

      const food = await res.json()

      navigate(`/food/${food._id}`)

    } catch(error) {
        console.error(error.message)
    }


  }

  return (
    <div className={classes.container}>
      <div className={classes.wrapper}>
        <h2 className={classes.title}>Craete food</h2>
        <form onSubmit={handleCreateProduct} encType='multipart/form-data'>
          <div className={classes.inputWrapper}>
            <label>Title: </label>
            <input type="text" 
              placeholder='Title...' 
              className={classes.input} 
              onChange={(e)=> setTitle(e.target.value)}/>
            
          </div>
          <div className={classes.inputWrapper}>
            <label>Description: </label>
            <input type="text" 
              placeholder='Description...' 
              className={classes.input} 
              onChange={(e)=> setDesc(e.target.value)}/>
          
          </div><div className={classes.inputWrapper}>
            <label>category: </label>
            <input type="text" 
              placeholder='Category...' 
              className={classes.input} 
              onChange={(e)=> setCategory(e.target.value)}/>
          </div>
          <div className={classes.inputWrapperImage}>
            <label htmlFor='image' className={classes.lableFileInput}>Image: <span>Upload here</span></label>
            <input type="file" 
              id="image"
              placeholder='Image...' 
              className={classes.input} 
              onChange={onChangeFile}
              style={{display:'none'}}
              />
              {image && <p className={classes.imageName}>{image.name} <AiOutlineCloseCircle onClick={handleCloseImg} className={classes.closeIcon}/></p>}
          </div>
          <div className={classes.inputWrapper}>
            <label>Price: </label>
            <input type="number" 
              step={0.01}
              placeholder='Price...' 
              className={classes.input} 
              onChange={(e)=> setPrice(e.target.value)}/>
          </div>
          <div className={classes.inputWrapper}>
            <label>review: </label>
            <input type="number" 
              step={0.1}
              min={1}
              max={5}
              placeholder='review...' 
              className={classes.input} 
              onChange={(e)=> setReview(e.target.value)}/>
          </div>
          <div>
            <div className={classes.buttonWrapper}>
              <button type="submit" className={classes.submitBtn}>
                Submit
              </button>
            </div>
          </div>
            
        </form>
      </div>
    </div>
  )
}

export default Create
