import React from 'react'
import classes from './create.moule.css'
import {AiOutlineCloseCircle} from 'react-icons/ai'
import { useState } from 'react'

const Create = () => {
  const [title, setTitle] = useState("")
  const [desc, setDesc] = useState("")
  const [Categoty, setCategoty] = useState("")
  const [image, setImage] = useState("")
  const [Price, setPrice] = useState("")
  const [Review, setReview] = useState("")

  const onChangeFile = (e) => {

  }

  const handleCloseImg = (e) => {
    
  }

  const handleCreateProduct = async(e) => {

  }

  return (
    <div className={classes.container}>
      <div className={classes.wrapper}>
        <h2 className={classes.title}>Craete food</h2>
        <form onSubmit={handleCreateProduct} encType='multipart/form-data'>
          <div className={classes.inpurWrapper}>
            <label>Title: </label>
            <input type="text" 
              placeholder='Title...' 
              className={classes.input} 
              onChange={(e)=> setTitle(e.target.value)}/>
            
          </div>
          <div className={classes.inpurWrapper}>
            <label>Description: </label>
            <input type="text" 
              placeholder='Description...' 
              className={classes.input} 
              onChange={(e)=> setDesc(e.target.value)}/>
          
          </div><div className={classes.inpurWrapper}>
            <label>Categoty: </label>
            <input type="text" 
              placeholder='Categoty...' 
              className={classes.input} 
              onChange={(e)=> setCategoty(e.target.value)}/>
          </div>
          <div className={classes.inpurWrapperImage}>
            <label htmlFor='image' className={classes.lableFileInput}>Image: <span>Upload here</span></label>
            <input type="file" 
              id="image"
              placeholder='Image...' 
              className={classes.input} 
              onChange={onChangeFile}
              style={{display:none}}
              />
              {image && <p className={classes.imageName}>{image.name} <AiOutlineCloseCircle onClick={handleCloseImg} className={classes.closeIcon}/></p>}
          </div>
          <div className={classes.inpurWrapper}>
            <label>Price: </label>
            <input type="number" 
              step={0.01}
              placeholder='Price...' 
              className={classes.input} 
              onChange={(e)=> setPrice(e.target.value)}/>
          </div>
          <div className={classes.inpurWrapper}>
            <label>Review: </label>
            <input type="number" 
              step={0.1}
              min={1}
              max={5}
              placeholder='Review...' 
              className={classes.input} 
              onChange={(e)=> setReview(e.target.value)}/>
          </div>

        </form>
      </div>
    </div>
  )
}

export default Create
